import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { userService } from '../services/userService';
import './Auth.css';
import { FaBriefcase, FaSearch, FaUserTie } from 'react-icons/fa';

function Login() {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      await userService.login(formData.email, formData.password);
      navigate('/jobs');
    } catch (error) {
      setError(error.message);
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  return (
    <div className="auth-container">
      <div className="auth-image">
        <div className="floating-icons">
          <div className="icon-item icon-1">
            <FaSearch size={24} color="#2196f3" />
          </div>
          <div className="icon-item icon-2">
            <FaBriefcase size={24} color="#2196f3" />
          </div>
          <div className="icon-item icon-3">
            <FaUserTie size={24} color="#2196f3" />
          </div>
        </div>
        <div className="auth-image-content">
          <h1>Find Your Dream Job</h1>
          <p>Connect with the best opportunities across the globe</p>
        </div>
      </div>
      <div className="auth-form-container">
        <div className="auth-form-wrapper">
          <h2>Welcome Back</h2>
          <p className="auth-subtitle">Please login to your account</p>
          
          {error && <div className="error-message">{error}</div>}
          
          <form onSubmit={handleSubmit} className="auth-form">
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email"
                required
              />
            </div>
            
            <div className="form-group">
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                placeholder="Enter your password"
                required
              />
            </div>
            
            <div className="form-group remember-forgot">
              <label className="remember">
                <input type="checkbox" /> Remember me
              </label>
              <a href="#" className="forgot-password">Forgot Password?</a>
            </div>
            
            <button type="submit" className="auth-button">Login</button>
          </form>
          
          <p className="auth-switch">
            Don't have an account? <Link to="/signup">Sign up</Link>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
