import React from 'react';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import './JobCard.css';

function JobCard({ job }) {
  const formatDate = (timestamp) => {
    const date = new Date(timestamp);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    
    if (diffDays === 0) return 'Today';
    if (diffDays === 1) return 'Yesterday';
    return `${diffDays} days ago`;
  };

  return (
    <div className="job-card">
      <div className="job-card-header">
        <div className="company-logo">
          <img src={job.employer_logo} alt={job.employer_name} />
        </div>
        <div className="company-info">
          <h3 className="company-name">{job.employer_name}</h3>
          <div className="location">
            <LocationOnIcon fontSize="small" />
            <span>{job.job_city}, {job.job_country}</span>
          </div>
        </div>
        <div className="salary">
          <span>{job.estimated_salary}</span>
        </div>
      </div>

      <h2 className="job-title">{job.job_title}</h2>

      <div className="skills-container">
        {job.job_required_skills.slice(0, 4).map((skill, index) => (
          <span key={index} className="skill-tag">{skill}</span>
        ))}
      </div>

      <div className="job-card-footer">
        <span className="posted-date">
          Posted {formatDate(job.job_posted_at_timestamp)}
        </span>
        <button 
          className="apply-button"
          onClick={() => window.open(job.job_apply_link, '_blank')}
        >
          Apply Now
        </button>
      </div>
    </div>
  );
}

export default JobCard;
