import React from 'react';
import { useParams } from 'react-router-dom';
import './JobDetails.css';

function JobDetails({ jobs }) {
  const { jobId } = useParams();
  const job = jobs.find(job => job.id === jobId);

  if (!job) {
    return <div>Job not found</div>;
  }

  return (
    <div className="job-details-container">
      <h1>{job.job_title}</h1>
      <img src={job.employer_logo} alt={`${job.employer_name} logo`} />
      <h2>{job.employer_name}</h2>
      <p><strong>Location:</strong> {job.job_city}, {job.job_country}</p>
      <p><strong>Skills:</strong> {job.job_required_skills.join(', ')}</p>
      <p><strong>Estimated Salary:</strong> {job.estimated_salary}</p>
      <p><strong>Posted On:</strong> {new Date(job.job_posted_at_timestamp).toLocaleDateString()}</p>
      <a href={job.job_apply_link} target="_blank" rel="noopener noreferrer" className="apply-button">Apply Now</a>
    </div>
  );
}

export default JobDetails;
