import React from 'react';
import './JobCard.css';

function JobCard({ job }) {
  return (
    <div className="job-card">
      <div className="job-header">
        <div className="company-logo">
          <img src={job.employer_logo || '/default-logo.png'} alt={job.employer_name} />
        </div>
        <div className="job-meta">
          <h2 className="company-name">{job.employer_name}</h2>
          <p className="location">{job.job_city}, {job.job_country}</p>
        </div>
        <div className="job-lpa">
          {job.job_min_salary && (
            <span>{(job.job_min_salary/100000).toFixed(1)} LPA</span>
          )}
        </div>
      </div>
      
      <h3 className="job-title">{job.job_title}</h3>
      
      <div className="job-tags">
        {job.job_required_skills?.slice(0, 4).map((skill, index) => (
          <span key={index} className="tag">{skill}</span>
        ))}
      </div>
      
      <div className="job-footer">
        <div className="job-timing">
          <span>Posted {new Date(job.job_posted_at_timestamp).toLocaleDateString()}</span>
        </div>
        <button className="apply-button" onClick={() => window.open(job.job_apply_link)}>
          Apply Now
        </button>
      </div>
    </div>
  );
}

export default JobCard;
