import React, { useState, useEffect } from 'react';
import JobCard from './JobCard';
import axios from 'axios';
import CircularProgress from '@mui/material/CircularProgress';
import './JobList.css';

function JobList() {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchJobs = async () => {
      const options = {
        method: 'GET',
        url: 'https://jsearch.p.rapidapi.com/search',
        params: {
          query: 'Software Developer in India',
          page: '1',
          num_pages: '1',
          date_posted: 'today'
        },
        headers: {
          'X-RapidAPI-Key': '87642e8656msh75f1750380c12d7p1bb310jsne0722bd56669',
          'X-RapidAPI-Host': 'jsearch.p.rapidapi.com'
        }
      };

      try {
        const response = await axios.request(options);
        const processedJobs = response.data.data.map(job => ({
          ...job,
          employer_logo: job.employer_logo || '/company-placeholder.png',
          job_city: job.job_city || 'Remote',
          job_country: 'India',
          job_required_skills: job.job_required_skills || ['Not Specified'],
          estimated_salary: job.job_min_salary ? 
            `${(job.job_min_salary/100000).toFixed(1)} LPA` : 
            'Not Disclosed'
        }));
        setJobs(processedJobs);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching jobs:', error);
        setError('Failed to load jobs. Please try again later.');
        setLoading(false);
      }
    };

    fetchJobs();
  }, []);

  if (loading) {
    return (
      <div className="loading-container">
        <CircularProgress />
        <p>Loading jobs...</p>
      </div>
    );
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div className="job-list-container">
      <div className="job-list">
        {jobs.map((job, index) => (
          <JobCard key={index} job={job} />
        ))}
      </div>
    </div>
  );
}

export default JobList;
